    private JSONObject getJsonFromStr(String str) {
        return JSONObject.parseObject(str);
    }

    public static boolean compareJsonArray(String str1, String str2) {
        JSONArray json1 = getJsonArrayFromStr(str1);
        JSONArray json2 = getJsonArrayFromStr(str2);
        Map<String, String> map1 = jsonArrayToMap(json1);
        Map<String, String> map2 = jsonArrayToMap(json2);
        // 比较两个map中对应的 key value 是否相等

        boolean result = map1.equals(map2);
        if (result) {
            return result;
        }

        return compare(map1, map2) && compare2(map2, map1);
    }

    private static List<String> ignoreKeys = Arrays.asList("USED_REPLENISHMENT", "INVENTORY_TALLY_TYPE", "REPLENISHMENT_TYPE");

    private static boolean compare(Map<String, String> map1, Map<String, String> map2) {
        AtomicBoolean result = new AtomicBoolean(true);
        map1.entrySet().stream().forEach(entry -> {
            if (!map2.containsKey(entry.getKey())) {
                result.set(false);
                System.out.println("key:" + entry.getKey() + " value:" + entry.getValue() + " is not exist in map2");
            }

            if (map2.get(entry.getKey()) == null) {
                if (entry.getValue() != null) {
                    result.set(false);
                    System.out.println("key:" + entry.getKey() + " value:" + entry.getValue() + " is not equal");
                    return;
                }
                return;
            }

            if (!map2.get(entry.getKey()).equals(entry.getValue())) {
                System.out.println("key:" + entry.getKey() + " is not equal");
                System.out.println("key:" + entry.getKey() + " value:" + map2.get(entry.getKey()) + " is not equal");
                System.out.println("key:" + entry.getKey() + " value:" + entry.getValue() + " is not equal");
                if (!ignoreKeys.contains(entry.getKey())) {
                    result.set(false);
                    System.out.println("key:" + entry.getKey() + " value:" + entry.getValue() + " is not exist in map1");

                }
            }
        });

        return result.get();
    }

    private static boolean compare2(Map<String, String> map1, Map<String, String> map2) {
        AtomicBoolean result = new AtomicBoolean(true);
        map1.entrySet().stream().forEach(entry -> {
            if (!map2.containsKey(entry.getKey())) {
                if (!ignoreKeys.contains(entry.getKey())) {
                    result.set(false);
                    System.out.println("key:" + entry.getKey() + " value:" + entry.getValue() + " is not exist in map1");

                }
            }
        });
        return result.get();
    }

    public static Map<String, String> jsonArrayToMap(JSONArray array) {
        Map<String, String> map = new HashMap<>();
        array.stream().forEach(item -> {
            JSONObject jsonObject = (JSONObject) item;
            map.put(jsonObject.getString("configCode"), jsonObject.getString("configValue"));
//            map.put(jsonObject.getString("configId"), jsonObject.getString("configValue"));

        });
        return map;
    }

